function initNavbar() {
    var a = 750, t = 50, n = "swing";
    $("#navbar-top .navbar-default ul.nav").onePageNav({
        currentClass: "active",
        changeHash: !1,
        scrollSpeed: a,
        scrollOffset: t,
        scrollThreshold: .5,
        filter: ":not(.external)",
        easing: n
    }); 
    $(".nav-external").click(function(e) {
        e.preventDefault(), 
        $("html, body").stop().animate({
            scrollTop: $($(this).attr("href")).offset().top - t
        }, a, n);
    }); 
    $("#navbar-top .navbar-default").affix({
        offset: {
            top: $("#home").height()
        }
    });
}

function initAnimations() {
    $(".animated").appear(function() {
        var a = $(this),
            t = a.data("animation"),
            n = a.data("delay");
        n ? setTimeout(function() {
            a.addClass(t), a.addClass("showing"), a.removeClass("hiding");
        }, n) : (a.addClass(t), a.addClass("showing"), a.removeClass("hiding"));
    }, {
        accY: -60
    }), 
    $(".skill").hover(function() {
        $("i", this).addClass("animated tada");
    }, function() {
        $("i", this).removeClass("animated tada");
    });
}
$(document).ready(function() {
    initNavbar(); 
    initAnimations();
    $(".nav2").sticky({topSpacing:34});
});
